/**
 * Todo App - Main Component
 * Manages todo list with CRUD operations, filtering, and editing
 * Includes user authentication, dashboard, and analytics
 */

import { useEffect, useState } from "react";
import axios from "axios";
import Auth from "./components/Auth";
import Dashboard from "./components/Dashboard";
import TaskManager from "./components/TaskManager";
import DailyChecklist from "./components/DailyChecklist";
import "./App.css";

// ===== API Configuration =====
const API_URL = "http://localhost:5000/api/todos";

// ===== Constants =====
const INITIAL_FORM_STATE = {
  text: "",
  description: "",
  priority: "medium",
  dueDate: "",
  category: "general"
};

const PRIORITY_COLORS = {
  high: "#ff4757",
  medium: "#ffa502",
  low: "#2ed573"
};

function App() {
  // ===== State Management =====
  const [user, setUser] = useState(null);
  const [todos, setTodos] = useState([]);
  const [formData, setFormData] = useState(INITIAL_FORM_STATE);
  const [filter, setFilter] = useState("all");
  const [editingId, setEditingId] = useState(null);
  const [activeTab, setActiveTab] = useState("checklist"); // 'checklist', 'tasks', 'dashboard'

  // ===== Lifecycle Hooks =====
  useEffect(() => {
    // Check if user is already logged in
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      setUser(JSON.parse(storedUser));
      fetchTodos();
    }
  }, []);

  // ===== API Functions =====

  /**
   * Fetch all todos from the API
   */
  const fetchTodos = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get(API_URL, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setTodos(response.data);
    } catch (error) {
      console.error("Error fetching todos:", error.message);
    }
  };

  /**
   * Add or update a todo
   */
  const saveTodo = async () => {
    if (!formData.text.trim()) {
      alert("Please enter a todo title");
      return;
    }

    try {
      const token = localStorage.getItem("token");
      const headers = { Authorization: `Bearer ${token}` };

      if (editingId) {
        // Update existing todo
        await axios.put(`${API_URL}/${editingId}`, {
          ...formData,
          dueDate: formData.dueDate || null
        }, { headers });
        setEditingId(null);
      } else {
        // Create new todo
        await axios.post(API_URL, {
          ...formData,
          dueDate: formData.dueDate || null
        }, { headers });
      }
      resetForm();
      fetchTodos();
    } catch (error) {
      console.error("Error saving todo:", error.message);
    }
  };

  /**
   * Toggle todo completion status
   */
  const toggleTodo = async (todo) => {
    try {
      const token = localStorage.getItem("token");
      await axios.put(`${API_URL}/${todo._id}`, {
        ...todo,
        completed: !todo.completed
      }, { headers: { Authorization: `Bearer ${token}` } });
      fetchTodos();
    } catch (error) {
      console.error("Error toggling todo:", error.message);
    }
  };

  /**
   * Load todo data into form for editing
   */
  const startEditingTodo = (todo) => {
    setFormData({
      text: todo.text,
      description: todo.description,
      priority: todo.priority,
      dueDate: todo.dueDate ? todo.dueDate.split('T')[0] : "",
      category: todo.category
    });
    setEditingId(todo._id);
  };

  /**
   * Delete a todo
   */
  const deleteTodo = async (id) => {
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`${API_URL}/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchTodos();
    } catch (error) {
      console.error("Error deleting todo:", error.message);
    }
  };

  /**
   * Handle logout
   */
  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    setUser(null);
    setTodos([]);
    resetForm();
  };

  /**
   * Handle successful authentication
   */
  const handleAuthSuccess = (userData) => {
    setUser(userData);
    fetchTodos();
  };

  // ===== Helper Functions =====

  /**
   * Reset form to initial state
   */
  const resetForm = () => {
    setFormData(INITIAL_FORM_STATE);
  };

  /**
   * Get color for priority badge
   */
  const getPriorityColor = (priority) => {
    return PRIORITY_COLORS[priority] || "#666";
  };

  /**
   * Filter todos based on selected filter
   */
  const getFilteredTodos = () => {
    return todos.filter(todo => {
      if (filter === "completed") return todo.completed;
      if (filter === "pending") return !todo.completed;
      return true;
    });
  };

  // ===== Computed Values =====
  const filteredTodos = getFilteredTodos();
  const completedCount = todos.filter(t => t.completed).length;
  const pendingCount = todos.filter(t => !t.completed).length;
  const isEditing = editingId !== null;

  // ===== Conditional Rendering =====
  if (!user) {
    return <Auth onAuthSuccess={handleAuthSuccess} />;
  }

  // ===== Render =====
  return (
    <div className="app-container">
      {/* Header with User Info */}
      <div className="header">
        <div className="header-left">
          <h1>üìã My Todo App</h1>
          <p className="subtitle">Organize your tasks efficiently</p>
        </div>
        <div className="header-right">
          <span className="user-info">üë§ {user.username}</span>
          <button className="logout-btn" onClick={handleLogout}>
            Logout
          </button>
        </div>
      </div>

      {/* Input Form */}
      <div className="input-section">
        <div className="input-group">
          <input
            className="input-field title-input"
            type="text"
            placeholder="Enter todo title..."
            value={formData.text}
            onChange={e => setFormData({ ...formData, text: e.target.value })}
            onKeyPress={e => e.key === "Enter" && saveTodo()}
          />
          <textarea
            className="input-field description-input"
            placeholder="Add description (optional)..."
            value={formData.description}
            onChange={e => setFormData({ ...formData, description: e.target.value })}
          />
        </div>

        <div className="form-row">
          <select
            className="form-control"
            value={formData.priority}
            onChange={e => setFormData({ ...formData, priority: e.target.value })}
          >
            <option value="low">Low Priority</option>
            <option value="medium">Medium Priority</option>
            <option value="high">High Priority</option>
          </select>

          <select
            className="form-control"
            value={formData.category}
            onChange={e => setFormData({ ...formData, category: e.target.value })}
          >
            <option value="general">General</option>
            <option value="work">Work</option>
            <option value="personal">Personal</option>
            <option value="shopping">Shopping</option>
          </select>

          <input
            type="date"
            className="form-control"
            value={formData.dueDate}
            onChange={e => setFormData({ ...formData, dueDate: e.target.value })}
          />

          <button className="btn btn-primary" onClick={saveTodo}>
            {isEditing ? "Update Todo" : "Add Todo"}
          </button>
        </div>
      </div>

      {/* Filter Buttons */}
      <div className="filter-section">
        <button
          className={`filter-btn ${filter === "all" ? "active" : ""}`}
          onClick={() => setFilter("all")}
        >
          All ({todos.length})
        </button>
        <button
          className={`filter-btn ${filter === "pending" ? "active" : ""}`}
          onClick={() => setFilter("pending")}
        >
          Pending ({pendingCount})
        </button>
        <button
          className={`filter-btn ${filter === "completed" ? "active" : ""}`}
          onClick={() => setFilter("completed")}
        >
          Completed ({completedCount})
        </button>
      </div>

      {/* Todos List */}
      <div className="todos-list">
        {filteredTodos.length === 0 ? (
          <div className="empty-state">
            <p>‚ú® No todos yet. Create one to get started!</p>
          </div>
        ) : (
          filteredTodos.map(todo => (
            <TodoCard
              key={todo._id}
              todo={todo}
              onToggle={toggleTodo}
              onEdit={startEditingTodo}
              onDelete={deleteTodo}
              getPriorityColor={getPriorityColor}
            />
          ))
        )}
      </div>
    </div>
  );
}

