import { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [todos, setTodos] = useState([]);
  const [text, setText] = useState("");
  const [description, setDescription] = useState("");
  const [priority, setPriority] = useState("medium");
  const [dueDate, setDueDate] = useState("");
  const [category, setCategory] = useState("general");
  const [filter, setFilter] = useState("all");
  const [editingId, setEditingId] = useState(null);

  const fetchTodos = async () => {
    try {
      const res = await axios.get("http://localhost:5000/todos");
      setTodos(res.data);
    } catch (err) {
      console.error("Error fetching todos:", err);
    }
  };

  useEffect(() => { fetchTodos(); }, []);

  const addTodo = async () => {
    if (!text.trim()) {
      alert("Please enter a todo title");
      return;
    }

    try {
      if (editingId) {
        await axios.put(`http://localhost:5000/todos/${editingId}`, {
          text,
          description,
          priority,
          dueDate: dueDate || null,
          category
        });
        setEditingId(null);
      } else {
        await axios.post("http://localhost:5000/todos", {
          text,
          description,
          priority,
          dueDate: dueDate || null,
          category
        });
      }
      setText("");
      setDescription("");
      setPriority("medium");
      setDueDate("");
      setCategory("general");
      fetchTodos();
    } catch (err) {
      console.error("Error saving todo:", err);
    }
  };

  const toggleTodo = async (todo) => {
    try {
      await axios.put(`http://localhost:5000/todos/${todo._id}`, {
        ...todo,
        completed: !todo.completed
      });
      fetchTodos();
    } catch (err) {
      console.error("Error toggling todo:", err);
    }
  };

  const editTodo = (todo) => {
    setText(todo.text);
    setDescription(todo.description);
    setPriority(todo.priority);
    setDueDate(todo.dueDate ? todo.dueDate.split('T')[0] : "");
    setCategory(todo.category);
    setEditingId(todo._id);
  };

  const deleteTodo = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/todos/${id}`);
      fetchTodos();
    } catch (err) {
      console.error("Error deleting todo:", err);
    }
  };

  const getPriorityColor = (priority) => {
    switch (priority) {
      case "high": return "#ff4757";
      case "medium": return "#ffa502";
      case "low": return "#2ed573";
      default: return "#666";
    }
  };

  const filteredTodos = todos.filter(todo => {
    if (filter === "completed") return todo.completed;
    if (filter === "pending") return !todo.completed;
    return true;
  });

  return (
    <div className="app-container">
      <div className="header">
        <h1>üìã My Todo App</h1>
        <p className="subtitle">Organize your tasks efficiently</p>
      </div>

      <div className="input-section">
        <div className="input-group">
          <input
            className="input-field title-input"
            placeholder="Enter todo title..."
            value={text}
            onChange={e => setText(e.target.value)}
            onKeyPress={e => e.key === "Enter" && addTodo()}
          />
          <textarea
            className="input-field description-input"
            placeholder="Add description (optional)..."
            value={description}
            onChange={e => setDescription(e.target.value)}
          />
        </div>

        <div className="form-row">
          <select
            className="form-control"
            value={priority}
            onChange={e => setPriority(e.target.value)}
          >
            <option value="low">Low Priority</option>
            <option value="medium">Medium Priority</option>
            <option value="high">High Priority</option>
          </select>

          <select
            className="form-control"
            value={category}
            onChange={e => setCategory(e.target.value)}
          >
            <option value="general">General</option>
            <option value="work">Work</option>
            <option value="personal">Personal</option>
            <option value="shopping">Shopping</option>
          </select>

          <input
            type="date"
            className="form-control"
            value={dueDate}
            onChange={e => setDueDate(e.target.value)}
          />

          <button className="btn btn-primary" onClick={addTodo}>
            {editingId ? "Update" : "Add Todo"}
          </button>
        </div>
      </div>

      <div className="filter-section">
        <button
          className={`filter-btn ${filter === "all" ? "active" : ""}`}
          onClick={() => setFilter("all")}
        >
          All ({todos.length})
        </button>
        <button
          className={`filter-btn ${filter === "pending" ? "active" : ""}`}
          onClick={() => setFilter("pending")}
        >
          Pending ({todos.filter(t => !t.completed).length})
        </button>
        <button
          className={`filter-btn ${filter === "completed" ? "active" : ""}`}
          onClick={() => setFilter("completed")}
        >
          Completed ({todos.filter(t => t.completed).length})
        </button>
      </div>

      <div className="todos-list">
        {filteredTodos.length === 0 ? (
          <div className="empty-state">
            <p>‚ú® No todos yet. Create one to get started!</p>
          </div>
        ) : (
          filteredTodos.map(todo => (
            <div
              key={todo._id}
              className={`todo-card ${todo.completed ? "completed" : ""}`}
            >
              <div className="todo-left">
                <input
                  type="checkbox"
                  className="todo-checkbox"
                  checked={todo.completed}
                  onChange={() => toggleTodo(todo)}
                />
                <div className="todo-content">
                  <h3 className="todo-title">{todo.text}</h3>
                  {todo.description && (
                    <p className="todo-description">{todo.description}</p>
                  )}
                  <div className="todo-meta">
                    <span
                      className="badge priority"
                      style={{ backgroundColor: getPriorityColor(todo.priority) }}
                    >
                      {todo.priority}
                    </span>
                    <span className="badge category">{todo.category}</span>
                    {todo.dueDate && (
                      <span className="badge due-date">
                        üìÖ {new Date(todo.dueDate).toLocaleDateString()}
                      </span>
                    )}
                  </div>
                </div>
              </div>
              <div className="todo-actions">
                <button
                  className="btn btn-edit"
                  onClick={() => editTodo(todo)}
                >
                  ‚úèÔ∏è
                </button>
                <button
                  className="btn btn-delete"
                  onClick={() => deleteTodo(todo._id)}
                >
                  üóëÔ∏è
                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default App;
