/**
 * Todo App - Main Component
 * Manages todo list with CRUD operations, filtering, and editing
 * Includes user authentication
 */

import { useEffect, useState } from "react";
import axios from "axios";
import Auth from "./components/Auth";
import "./App.css";

// ===== API Configuration =====
const API_URL = "http://localhost:5000/todos";

// ===== Constants =====
const INITIAL_FORM_STATE = {
  text: "",
  description: "",
  priority: "medium",
  dueDate: "",
  category: "general"
};

const PRIORITY_COLORS = {
  high: "#ff4757",
  medium: "#ffa502",
  low: "#2ed573"
};

function App() {
  // ===== State Management =====
  const [user, setUser] = useState(null);
  const [todos, setTodos] = useState([]);
  const [formData, setFormData] = useState(INITIAL_FORM_STATE);
  const [filter, setFilter] = useState("all");
  const [editingId, setEditingId] = useState(null);

  // ===== Lifecycle Hooks =====
  useEffect(() => {
    // Check if user is already logged in
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      setUser(JSON.parse(storedUser));
      fetchTodos();
    }
  }, []);

  // ===== Computed Values =====
  const filteredTodos = getFilteredTodos();
  const completedCount = todos.filter(t => t.completed).length;
  const pendingCount = todos.filter(t => !t.completed).length;
  const isEditing = editingId !== null;

  // ===== Conditional Rendering =====
  if (!user) {
    return <Auth onAuthSuccess={handleAuthSuccess} />;
  }

  // ===== Render =====
  return (
    <div className="app-container">
      {/* Header with User Info */}
      <div className="header">
        <div className="header-left">
          <h1>üìã My Todo App</h1>
          <p className="subtitle">Organize your tasks efficiently</p>
        </div>
        <div className="header-right">
          <span className="user-info">üë§ {user.username}</span>
          <button className="logout-btn" onClick={handleLogout}>
            Logout
          </button>
        </div>
      </div>

      {/* Input Form */}
      <div className="input-section">
        <div className="input-group">
          <input
            className="input-field title-input"
            type="text"
            placeholder="Enter todo title..."
            value={formData.text}
            onChange={e => setFormData({ ...formData, text: e.target.value })}
            onKeyPress={e => e.key === "Enter" && saveTodo()}
          />
          <textarea
            className="input-field description-input"
            placeholder="Add description (optional)..."
            value={formData.description}
            onChange={e => setFormData({ ...formData, description: e.target.value })}
          />
        </div>

        <div className="form-row">
          <select
            className="form-control"
            value={formData.priority}
            onChange={e => setFormData({ ...formData, priority: e.target.value })}
          >
            <option value="low">Low Priority</option>
            <option value="medium">Medium Priority</option>
            <option value="high">High Priority</option>
          </select>

          <select
            className="form-control"
            value={formData.category}
            onChange={e => setFormData({ ...formData, category: e.target.value })}
          >
            <option value="general">General</option>
            <option value="work">Work</option>
            <option value="personal">Personal</option>
            <option value="shopping">Shopping</option>
          </select>

          <input
            type="date"
            className="form-control"
            value={formData.dueDate}
            onChange={e => setFormData({ ...formData, dueDate: e.target.value })}
          />

          <button className="btn btn-primary" onClick={saveTodo}>
            {isEditing ? "Update Todo" : "Add Todo"}
          </button>
        </div>
      </div>

      {/* Filter Buttons */}
      <div className="filter-section">
        <button
          className={`filter-btn ${filter === "all" ? "active" : ""}`}
          onClick={() => setFilter("all")}
        >
          All ({todos.length})
        </button>
        <button
          className={`filter-btn ${filter === "pending" ? "active" : ""}`}
          onClick={() => setFilter("pending")}
        >
          Pending ({pendingCount})
        </button>
        <button
          className={`filter-btn ${filter === "completed" ? "active" : ""}`}
          onClick={() => setFilter("completed")}
        >
          Completed ({completedCount})
        </button>
      </div>

      {/* Todos List */}
      <div className="todos-list">
        {filteredTodos.length === 0 ? (
          <div className="empty-state">
            <p>‚ú® No todos yet. Create one to get started!</p>
          </div>
        ) : (
          filteredTodos.map(todo => (
            <TodoCard
              key={todo._id}
              todo={todo}
              onToggle={toggleTodo}
              onEdit={startEditingTodo}
              onDelete={deleteTodo}
              getPriorityColor={getPriorityColor}
            />
          ))
        )}
      </div>
    </div>
  );
}

/**
 * TodoCard Component
 * Displays a single todo item with actions
 */
function TodoCard({ todo, onToggle, onEdit, onDelete, getPriorityColor }) {
  return (
    <div className={`todo-card ${todo.completed ? "completed" : ""}`}>
      <div className="todo-left">
        <input
          type="checkbox"
          className="todo-checkbox"
          checked={todo.completed}
          onChange={() => onToggle(todo)}
          aria-label="Mark todo as complete"
        />
        <div className="todo-content">
          <h3 className="todo-title">{todo.text}</h3>
          {todo.description && (
            <p className="todo-description">{todo.description}</p>
          )}
          <div className="todo-meta">
            <span
              className="badge priority"
              style={{ backgroundColor: getPriorityColor(todo.priority) }}
            >
              {todo.priority}
            </span>
            <span className="badge category">{todo.category}</span>
            {todo.dueDate && (
              <span className="badge due-date">
                üìÖ {new Date(todo.dueDate).toLocaleDateString()}
              </span>
            )}
          </div>
        </div>
      </div>
      <div className="todo-actions">
        <button
          className="btn btn-edit"
          onClick={() => onEdit(todo)}
          aria-label="Edit todo"
        >
          ‚úèÔ∏è
        </button>
        <button
          className="btn btn-delete"
          onClick={() => onDelete(todo._id)}
          aria-label="Delete todo"
        >
          üóëÔ∏è
        </button>
      </div>
    </div>
  );
}

export default App;
